'use strict';var processFontVariant=require('../processFontVariant');describe('processFontVariant',function(){it('should accept arrays',function(){expect(processFontVariant([])).toEqual([]);expect(processFontVariant(['oldstyle-nums'])).toEqual(['oldstyle-nums']);expect(processFontVariant(['proportional-nums','lining-nums'])).toEqual(['proportional-nums','lining-nums']);});it('should accept string values',function(){expect(processFontVariant('oldstyle-nums')).toEqual(['oldstyle-nums']);expect(processFontVariant('lining-nums  ')).toEqual(['lining-nums']);expect(processFontVariant('   tabular-nums')).toEqual(['tabular-nums']);});it('should accept string with multiple values',function(){expect(processFontVariant('oldstyle-nums lining-nums')).toEqual(['oldstyle-nums','lining-nums']);expect(processFontVariant('proportional-nums  oldstyle-nums   lining-nums')).toEqual(['proportional-nums','oldstyle-nums','lining-nums']);expect(processFontVariant('   small-caps proportional-nums  oldstyle-nums lining-nums')).toEqual(['small-caps','proportional-nums','oldstyle-nums','lining-nums']);});});