'use strict';describe('PixelRatio',function(){var Dimensions=require('../Dimensions').default;var PixelRatio=require('../PixelRatio').default;beforeEach(function(){Dimensions.set({windowPhysicalPixels:{width:400,height:800,scale:2,fontScale:3}});});it('should give the pixel density',function(){expect(PixelRatio.get()).toEqual(2);});it('should give the font scale when present',function(){expect(PixelRatio.getFontScale()).toEqual(3);});it('should give the pixel density instead of the font scale when the front scale is not present',function(){Dimensions.set({windowPhysicalPixels:{scale:2}});expect(PixelRatio.getFontScale()).toEqual(2);});it('should convert a layout size to pixel size',function(){expect(PixelRatio.getPixelSizeForLayoutSize(400)).toEqual(800);});it('should round a layout size to pixel size',function(){expect(PixelRatio.roundToNearestPixel(8.4)).toEqual(8.5);});});