'use strict';var PlatformAndroid=require('../Platform.android');var PlatformIOS=require('../Platform.ios');describe('Platform',function(){describe('OS',function(){it('should have correct value',function(){expect(PlatformIOS.OS).toEqual('ios');expect(PlatformAndroid.OS).toEqual('android');});});describe('select',function(){it('should return platform specific value',function(){var obj={ios:'ios',android:'android'};expect(PlatformIOS.select(obj)).toEqual(obj.ios);expect(PlatformAndroid.select(obj)).toEqual(obj.android);});it('should return native value if no specific value was found',function(){var obj={native:'native',default:'default'};expect(PlatformIOS.select(obj)).toEqual(obj.native);expect(PlatformAndroid.select(obj)).toEqual(obj.native);});it('should return default value if no specific value was found',function(){var obj={default:'default'};expect(PlatformIOS.select(obj)).toEqual(obj.default);expect(PlatformAndroid.select(obj)).toEqual(obj.default);});});});