'use strict';var SceneTracker=require('../SceneTracker');describe('setActiveScene',function(){it('can handle multiple listeners and unsubscribe',function(){var listeners=[jest.fn(),jest.fn(),jest.fn()];var subscriptions=listeners.map(function(listener){return SceneTracker.addActiveSceneChangedListener(listener);});subscriptions[1].remove();var newScene={name:'scene1'};SceneTracker.setActiveScene(newScene);expect(listeners[0]).toBeCalledWith(newScene);expect(listeners[1]).not.toBeCalled();expect(listeners[2]).toBeCalledWith(newScene);});});