'use strict';describe('mapWithSeparator',function(){var mapWithSeparator=require('../mapWithSeparator');it('mapWithSeparator returns expected results',function(){var array=[1,2,3];var result=mapWithSeparator(array,function(value){return value*2;},function(){return 0;});expect(result).toEqual([2,0,4,0,6]);});it('mapWithSeparator indices are correct',function(){var array=[1,2,3];var result=mapWithSeparator(array,function(value,index){return index;},function(index){return index;});expect(result).toEqual([0,0,1,1,2]);});it('mapWithSeparator passes correct array and indices',function(){var array=[3,2,1];var result=mapWithSeparator(array,function(value,index,arr){return arr[index];},function(index){return index;});expect(result).toEqual([3,0,2,1,1]);});it('mapWithSeparator returns empty array when empty array is given as input',function(){var array=[];var result=mapWithSeparator(array,function(value){return value*2;},function(){return 0;});expect(result).toEqual([]);});});