'use strict';var processAspectRatio=require('../processAspectRatio');describe('processAspectRatio',function(){it('should accept numbers',function(){expect(processAspectRatio(1)).toBe(1);expect(processAspectRatio(0)).toBe(0);expect(processAspectRatio(1.5)).toBe(1.5);});it('should accept string numbers',function(){expect(processAspectRatio('1')).toBe(1);expect(processAspectRatio('0')).toBe(0);expect(processAspectRatio('1.5')).toBe(1.5);expect(processAspectRatio('+1.5')).toBe(1.5);expect(processAspectRatio('   1')).toBe(1);expect(processAspectRatio('   0    ')).toBe(0);});it('should accept `auto`',function(){expect(processAspectRatio('auto')).toBe(undefined);expect(processAspectRatio(' auto')).toBe(undefined);expect(processAspectRatio(' auto  ')).toBe(undefined);});it('should accept ratios',function(){expect(processAspectRatio('+1/1')).toBe(1);expect(processAspectRatio('0 / 10')).toBe(0);expect(processAspectRatio('117/ 13')).toBe(9);expect(processAspectRatio('1.5 /1.2')).toBe(1.25);expect(processAspectRatio('1/0')).toBe(Infinity);});it('should not accept invalid formats',function(){expect(function(){return processAspectRatio('0a');}).toThrowErrorMatchingSnapshot();expect(function(){return processAspectRatio('1 / 1 1');}).toThrowErrorMatchingSnapshot();expect(function(){return processAspectRatio('auto 1/1');}).toThrowErrorMatchingSnapshot();});it('should ignore non string falsy types',function(){var invalidThings=[undefined,null,false];invalidThings.forEach(function(thing){expect(processAspectRatio(thing)).toBe(undefined);});});it('should not accept non string truthy types',function(){var invalidThings=[function(){},[1,2,3],{}];invalidThings.forEach(function(thing){expect(function(){return processAspectRatio(thing);}).toThrowErrorMatchingSnapshot();});});});