'use strict';describe('Dimensions',function(){var Dimensions=require('../Dimensions').default;var Platform=require('../Platform');it('should set window dimensions',function(){Dimensions.set({windowPhysicalPixels:{width:400,height:800,scale:2,fontScale:3}});expect(Dimensions.get('window').width).toEqual(200);expect(Dimensions.get('window').height).toEqual(400);expect(Dimensions.get('window').scale).toEqual(2);expect(Dimensions.get('window').fontScale).toEqual(3);});it('should set screen dimensions on Android',function(){Platform.OS='android';var dimensions={width:400,height:800,scale:2,fontScale:3};Dimensions.set({windowPhysicalPixels:dimensions,screenPhysicalPixels:dimensions});expect(Dimensions.get('screen').width).toEqual(200);expect(Dimensions.get('screen').height).toEqual(400);expect(Dimensions.get('screen').scale).toEqual(2);expect(Dimensions.get('screen').fontScale).toEqual(3);});it('should set screen dimensions on iOS',function(){Platform.OS='ios';var dimensions={width:400,height:800,scale:2,fontScale:3};Dimensions.set({windowPhysicalPixels:dimensions});expect(Dimensions.get('screen')).toEqual(Dimensions.get('window'));});});